import React, { useContext , useState }  from 'react'
import MovieContext from "../contexts/MovieContext"
import "./styles/form.css"

const Form = () => {
    const { config } = useContext(MovieContext)
    const context = useContext(MovieContext)
    let new_object = []
    const [name, setName] = useState('');
    let new_relacao = {}
    
    const handleSubmit = (e) => {
    
        e.preventDefault();
        console.log(e)
        console.log(`Form submitted, ${name}`);
        new_relacao = 
        new_object = {
            ...config,
            ocupados: [...config.ocupados.concat(config.seatNumbers)],
            seatNumbers: [],
            totalSeats: 0,
            relacao: {...config.relacao,}
        }
        context.changeState(new_object)
        console.log(new_object)

    }

    return(
        <form onSubmit = {handleSubmit}>

            <ul className="form-style-1">
                <li><input type="text" name="field1" className="field-divided" placeholder="Nome"  onChange = {(e) => setName(e.target.value)} value = {name}/>
                    <input type="text" name="field2" className="field-divided" placeholder="Sobrenome" /></li>
               
                <li>
                    <input type="submit" value="Reservar!" className="submitButton" />
                </li>
            </ul>

        </form>
    );
    
}
export default Form;
