import React, { useContext , useState }  from 'react'
import MovieContext from "../contexts/MovieContext"
import "./styles/form.css"

const Form = () => {
    const { config } = useContext(MovieContext)
    const context = useContext(MovieContext)
    let new_object = []
    const [name, setName] = useState('');
    
    const handleSubmit = (e) => {
    
        e.preventDefault();

        console.log(`Form submitted, ${name}`);
        new_object = {
            ...config,
            ocupados: [...config.ocupados.concat(config.seatNumbers)],
            seatNumbers: [],
            totalSeats: 0,
            relacao: {...config.relacao,}
        }
        context.changeState(new_object)
        console.log(new_object)

    }

    return(
        <form onSubmit = {handleSubmit}>

            <ul class="form-style-1">
                <li><label>Nome do Responsável pela marcação <span class="required">*</span></label><input type="text" name="field1" class="field-divided" placeholder="Nome" /> <input type="text" name="field2" class="field-divided" placeholder="Sobrenome" /></li>
               
                <li>
                    <input type="submit" value="Reservar!" />
                </li>
            </ul>

        </form>
    );
    
}

export default Form;
