import React, { useContext , useState }  from 'react'
import MovieContext from "../contexts/MovieContext"
const Form = () => {
    const { config } = useContext(MovieContext)
    const context = useContext(MovieContext)
    let new_object = []
    const [name, setName] = useState('');
    
    const handleSubmit = (e) => {
    
        e.preventDefault();

        console.log(`Form submitted, ${name}`);
        new_object = {
            ...config,
            ocupados: [...config.ocupados.concat(config.seatNumbers)],
            seatNumbers: [],
            totalSeats: 0,
            relacao: {...config.relacao,}
        }
        context.changeState(new_object)
        console.log(new_object)

    }

    return(
        <form onSubmit = {handleSubmit}>

            <ul class="form-style-1">
                <li><label>Full Name <span class="required">*</span></label><input type="text" name="field1" class="field-divided" placeholder="First" /> <input type="text" name="field2" class="field-divided" placeholder="Last" /></li>
                <li>
                    <label>Email <span class="required">*</span></label>
                    <input type="email" name="field3" class="field-long" />
                </li>
                <li>
                    <label>Subject</label>
                    <select name="field4" class="field-select">
                    <option value="Advertise">Advertise</option>
                    <option value="Partnership">Partnership</option>
                    <option value="General Question">General</option>
                    </select>
                </li>
                <li>
                    <label>Your Message <span class="required">*</span></label>
                    <textarea name="field5" id="field5" class="field-long field-textarea"></textarea>
                </li>
                <li>
                    <input type="submit" value="Submit" />
                </li>
            </ul>

        </form>
    );
    
}

export default Form;
